import{_ as t}from"./img1-be51aa85.js";import{c as a,b as s,k as e,o as c,s as i,C as o}from"./index-3b9c6b2e.js";const r="/assets/img2-8a4f645e.png",l="/assets/img3-8ffc841e.png",m={class:"enery-sources"},n=e('<div class="container"><div class="py-12"><h2 class="text-primary">Законадательство в области ВИЭ</h2><p class="text-secondary"> В первую очередь необходимо разобраться с оборудованием и подобрать оптимальную солнечную электростанцию. Батареи не универсальны, поэтому перед покупкой и монтажом рекомендуем обратиться за консультацией к специалисту. Он расскажет вам о формировании цены &quot;зеленого&quot; тарифа (которая закреплена законодательно) и подберет модель, подходящую именно для вашего дома. </p><p class="text-secondary"> Не менее важную роль играет специальный счетчик с коммуникационным GSM/GPRS-модулем, который предназначен для подсчета выработанной/потраченной солнечной энергии. С его помощью информация о вашем энергообороте будет передаваться в Облэнерго. </p><div class="grid sm:grid-cols-2 md:grid-cols-3 gap-5 pt-6"><img class="full-image" src="'+t+'" alt="image"><img class="full-image" src="'+r+'" alt="image"><img class="full-image" src="'+l+'" alt="image"></div><div class="flex justify-center py-8"><button class="btn">Загрузить</button></div></div></div>',1),f={__name:"Sources",setup(p){return(_,d)=>(c(),a("section",m,[s(i),n,s(o)]))}};export{f as default};
